import map from 'lodash/map'

export default function emitAsync (emitter, event, ...args) {
  const errors = []
  const onError = error => {
    errors.push(error)
  }

  return Promise.all(map(
    emitter.listeners(event),
    listener => new Promise(resolve => {
      resolve(listener.apply(this, args))
    }).catch(onError)
  )).then(() => errors)
}
