const toDecorator = (wrapFunction, wrapMethod = wrapFunction) =>
  (target, key, descriptor) => key === undefined
    ? wrapFunction(target)
    : {
      ...descriptor,
      value: (
        typeof target === 'function'
          ? wrapFunction
          : wrapMethod
      )(descriptor.value)
    }
export { toDecorator as default }
